			,[Due]
			,[Delivery]
			,[Adjustment]
			,[Locker]
			,[Closing]
			,[CashBoxAmount]
			,[CashByAccount]
			,[CashStatus]
			,PreDelivery
			,PreReceived)
	VALUES
		   (@CashID	,
			GETDATE(),
			@ExecutiveId	,
			 @Thousand ,
           @FiveHundrad ,
           @Hundrad ,
           @Fifty ,
           @Twenty ,
           @Ten ,
           @Five ,
           @Two ,
           @One ,
           @Opening ,
           @Sales ,
           @Expense ,
